import React from 'react';
import '../style/imageSlider.css';


class ImageSlider extends React.Component {
    state = {
      currentIndex: 0,
    };
  
    // Functions to handle scrolling to the previous and next images
    goToPrevImage = () => {
      this.setState((prevState) => ({
        currentIndex: Math.max(0, prevState.currentIndex - 1),
      }));
    };
  
    goToNextImage = () => {
      this.setState((prevState) => ({
        currentIndex: Math.min(this.props.images.length - 1, prevState.currentIndex + 1),
      }));
    };
  
    render() {
      const { currentIndex } = this.state;
      const { images } = this.props;
  
      return (
        <div className="image-slider">
          <div className="image-container">
            {images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`Image ${index + 1}`}
                className={`image ${index === currentIndex ? 'active' : ''}`}
              />
            ))}
          </div>
          <button onClick={this.goToPrevImage} disabled={currentIndex === 0}>
            Prev
          </button>
          <button onClick={this.goToNextImage} disabled={currentIndex === images.length - 1}>
            Next
          </button>
        </div>
      );
    }
  }
  
  export default ImageSlider;
