import React, { useState } from 'react';

const ContactForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleGmailClick = () => {
    const mailSubject = encodeURIComponent(subject);
    const mailMessage = encodeURIComponent(message);
    const mailtoLink = `mailto:?subject=${mailSubject}&body=${mailMessage}`;
    window.open(mailtoLink);
  };

  const handleDesktopMailClick = () => {
    const mailSubject = encodeURIComponent(subject);
    const mailMessage = encodeURIComponent(message);
    const mailtoLink = `mailto:contact@example.com?subject=${mailSubject}&body=${mailMessage}`;
    window.open(mailtoLink);
  };

  return (
    <form>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <label>
        Subject:
        <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} />
      </label>
      <label>
        Message:
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <div>
        <button type="button" onClick={handleGmailClick}>
          Send with Gmail
        </button>
        <button type="button" onClick={handleDesktopMailClick}>
          Send with Default Mail
        </button>
      </div>
    </form>
  );
};

export default ContactForm;
