import React, { useState } from 'react';

const ContactForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Construct the mailto link
    const mailSubject = encodeURIComponent(subject);
    const mailMessage = encodeURIComponent(message);
    let mailtoLink = '';

    if (navigator.userAgent.match(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i)) {
      // For mobile devices, open the default mail app
      mailtoLink = `mailto:?subject=${mailSubject}&body=${mailMessage}`;
    } else {
      // For desktop, let the user choose which mail service to open
      mailtoLink = `mailto:iangoodwin173.com?subject=${mailSubject}&body=${mailMessage}`;
    }

    // Open the email client
    window.open(mailtoLink);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <label>
        Subject:
        <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} />
      </label>
      <label>
        Message:
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default ContactForm;
