import React from 'react';
import '../App.css';


function Home() {

    const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    // Delay setting isMounted to true to allow the component to render first
    const timeout = setTimeout(() => {
      setIsMounted(true);
    }, 100);

    // Clear the timeout to avoid memory leaks
    return () => clearTimeout(timeout);
  }, []);
  return (
    <div className="App-header">
        
      <h1 className="title-text">Ian Goodwin: Full-Stack Developer</h1>
  
      
      <p className="tagline-text">
        Hi, I'm Ian. I'm a Full-Stack Developer that also enjoys gaming and golfing.
      </p>
    </div>
    
    
  );
}

export default Home;
