import '../App.css';
import '../style/home.css';
import React, { useEffect, useState } from 'react';


function Home() {

    const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    // Delay setting isMounted to true to allow the component to render first
    const timeout = setTimeout(() => {
      setIsMounted(true);
    }, 100);

    // Clear the timeout to avoid memory leaks
    return () => clearTimeout(timeout);
  }, []);

  const [text, setText] = useState('');
  const fullText = "I'm a Full-Stack Developer that also enjoys gaming and golfing."; // Replace this with your desired text

  useEffect(() => {
    let index = 0;

    const typingInterval = setInterval(() => {
      if (index === fullText.length) {
        clearInterval(typingInterval);
      } else {
        setText(fullText.slice(0, index + 1));
        index++;
      }
    }, 100); // Adjust the typing speed by changing the interval duration

    return () => clearInterval(typingInterval);
  }, [fullText]);
  return (
    <div className={`fade-in ${isMounted ? 'visible' : ''}`}>
        
      <h1 className="title-text">Ian Goodwin: Full-Stack Developer</h1>
  
      
      <p className="typing-text">
            {text}
      </p>
    </div>
    
    
  );
}

export default Home;
